INSERT INTO artikeltext (artikelnummer, locale, textart, artikeltext) VALUES ('122400', 'DE', 'DESC', 'Das Poi Paar Jester ist ein echter Blickfang. Der Schlaufengriff befindet sich direkt am Schwungkörper, Gesamtlänge ca. 190cm, mit drei Schweifen. Leuchten im Schwarzlicht. Inklusive Säckchen, die selbst befüllt werden müssen - wahlweise Reis/Sand/Hirse etc. Diese bilden das selbstbestimmbare Gewicht für das Poi und werden in den Kopf eingelegt.');
INSERT INTO artikeltext (artikelnummer, locale, textart, artikeltext) VALUES ('122500', 'DE', 'DESC', 'Ein elegantes Poi-Paar mit drei Schweifen. Länge des Schwungkörpers ca. 110cm, Schlaufengriff und Schnur ca. 40cm lang. Leuchten im Schwarzlicht. Inklusive Säckchen, die selbst befüllt werden müssen - wahlweise Reis/Sand/Hirse etc. Diese bilden das selbstbestimmbare Gewicht für das Poi und werden in den Kopf eingelegt.');
