INSERT INTO artikeltext (artikelnummer, locale, textart, artikeltext) VALUES ('201800', 'DE', 'DESC', 'Uni-Hockey Set 80cm, bestehend aus 2x4 Stöcken in blau und gelb, Länge 80cm, aus hochwertigem, flexiblem HDP-Material, dazu acht weiße Lochbälle. Für Menschen bis ca. 150cm Körpergröße.');
INSERT INTO artikeltext (artikelnummer, locale, textart, artikeltext) VALUES ('201900', 'DE', 'DESC', 'Uni-Hockey Set 73cm, bestehend aus 2x4 Stöcken in blau und gelb, Länge 73cm, aus hochwertigem, flexiblem HDP-Material, dazu vier farbige Lochbälle. Für Menschen bis ca. 135cm Körpergröße.');
INSERT INTO artikeltext (artikelnummer, locale, textart, artikeltext) VALUES ('202000', 'DE', 'DESC', 'Uni-Hockey Set 63cm, bestehend aus 2x4 Stöcken in blau und gelb, Länge 63cm, aus hochwertigem, flexiblem HDP-Material, dazu vier farbige Lochbälle. Für das Kindergartenalter.');
INSERT INTO artikeltext (artikelnummer, locale, textart, artikeltext) VALUES ('202100', 'DE', 'DESC', 'Top-Kreisel aus stabilem Kunststoff, rot. Durchm. 80cm, Tiefe 44cm. Für Außen und Innen geeignet. Auch für den Wasserbereich geeignet, da er schwimmt.');
INSERT INTO artikeltext (artikelnummer, locale, textart, artikeltext) VALUES ('202200', 'DE', 'DESC', 'Street Hockey Tor 84 x 71cm Stahlrohr. Das freistehende Street-Hockey Tor aus blauem, pulverbeschichtetem Stahlrohr wird überall dort aufgestellt, wo es gebraucht wird .');
INSERT INTO artikeltext (artikelnummer, locale, textart, artikeltext) VALUES ('202300', 'DE', 'DESC', 'Sich im Gleichschritt zu bewegen, ist manchmal nicht so einfach. Je mehr Personen mitmachen, desto schwieriger, aber auch lustiger wird es. Ein tolles Spiel- und Übungsgerät, das bei keinem Spielfest fehlen sollte. Äußerst stabiles Sperrholz, rutschfest, braun beschichtet, 21mm stark, 12cm breit.');
INSERT INTO artikeltext (artikelnummer, locale, textart, artikeltext) VALUES ('202400', 'DE', 'DESC', 'Klassisches Indiaca Set bestehend aus 2 Schlägern und einem Indiaca Play.');
INSERT INTO artikeltext (artikelnummer, locale, textart, artikeltext) VALUES ('202500', 'DE', 'DESC', 'Der klassische Federhandball.');
INSERT INTO artikeltext (artikelnummer, locale, textart, artikeltext) VALUES ('202600', 'DE', 'DESC', 'Acht Holzkugeln in vier Farben, Durchmesser 75mm, eine Zielkugel 40mm, Gewicht 2,3kg. Die original patentierten Holz-Boccia-Kugeln aus Italien werden aus einem Holz-Agglomerat hergestellt, sind stoß- und bruchfest und hitzebeständig, mit ungiftiger Lackierung, cadmium- und schadstofffrei, zertifiziert nach EN 71/88.');
INSERT INTO artikeltext (artikelnummer, locale, textart, artikeltext) VALUES ('202700', 'DE', 'DESC', 'Krocket Set, bestehend aus 4 Schlägern, 83cm lang und 4 Holzkugeln, Durchmesser 75mm, eine Start- und eine Zielstange, Metalltore. Im stabilen Tragekarton mit Spielanleitung, Gewicht 3,4kg. Für Kinder im Alter von 6-12 Jahren.');
INSERT INTO artikeltext (artikelnummer, locale, textart, artikeltext) VALUES ('202800', 'DE', 'DESC', 'Flingo - ob am Strand, im Park, auf dem Schulhof oder auf Reisen, dieses neue Spielerlebnis mit dem Bounce-Effekt ist einfach zu lernen und macht Spaß. Zwei elastische Spieltücher und ein Spielball bilden das Set.');
